mapper.select:
SELECT R_NO "r_no" , B_NO "b_no" , U_ID "u_id" , U_NAME "u_name" , CONTENT "content" , to_char( reg_date,'YYYY-MM-DD') "reg_date"
FROM REPLY WHERE b_no = #{b_no}
ORDER BY REG_DATE DESC

mapper.insert:
INSERT INTO REPLY 
(
	 	R_NO 		
	,   B_NO	
	,   U_ID    	
	,   U_NAME  	
	,   CONTENT 	
)
VALUES
(
		(select nvl(max(r_no),0) + 1 from REPLY)
	,	#{b_no}
	, 	#{u_id}
	,	#{u_name}
	,	#{content}
)

mapper.update:
UPDATE REPLY
SET	CONTENT = #{content}
WHERE r_no = #{r_no}
